mapper.sql:
SELECT DISTINCT main.businessPID, main.businessName, main.businessAddress, main.empName, main.expandTime, main.priceCoefficient from report main
and date(main.expandTime) BETWEEN date(#{startDate}) AND date(#{endDate})
and main.date = #{date}
and main.businessPID = #{businessPID}
and main.businessName LIKE CONCAT('%',#{businessName},'%')
and main.empName LIKE CONCAT('%',#{empName},'%')

mapper.select:
SELECT sub_report.businessPID, sub_report.businessName, sub_report.businessAddress, sub_report.empName, sub_report.expandTime, sub_report.priceCoefficient,
(SELECT activeClientNum FROM report ac WHERE ac.date = DATE_FORMAT(date_add(sub_report.expandTime, interval #{item.addDay} day), '%Y%m%d') AND ac.businessPID = sub_report.businessPID ) AS #{item.columnName}
FROM () AS sub_report ORDER BY sub_report.expandTime DESC
